{
  "$schema": "https://vega.github.io/schema/vega/v5.json",
  "description": "Connections Between Key Issues",
  "title": "Connections Between Key Issues",
  "width": 800,
  "height": 600,
  "padding": 5,

  "signals": [
    {"name": "cx", "update": "width / 2"},
    {"name": "cy", "update": "height / 2"},
    {"name": "nodeRadius", "value": 10}
  ],

  "data": [
    {
      "name": "node-data",
      "values": ["Crime", "Corruption", "Pollution", "Foreign Investment", "Emigration", "Dissatisfaction", "Military Trust", "Political Trust"]
    },
    {
      "name": "link-data",
      "values": [
        {"source": "Crime", "target": "Corruption"},
        {"source": "Dissatisfaction", "target": "Crime"},
        {"source": "Dissatisfaction", "target": "Corruption"},
        {"source": "Foreign Investment", "target": "Nationalism"},
        {"source": "Military Trust", "target": "Political Distrust"}
      ]
    },
    {
      "name": "nodes",
      "source": "node-data",
      "transform": [
        {"type": "force", "iterations": 300, "static": true, "forces": [
          {"force": "center", "x": {"signal": "cx"}, "y": {"signal": "cy"}},
          {"force": "collide", "radius": {"signal": "nodeRadius"}},
          {"force": "nbody", "strength": -100}
        ]}
      ]
    },
    {
      "name": "links",
      "source": "link-data",
      "transform": [
        {
          "type": "lookup",
          "from": "nodes",
          "key": "name",
          "fields": ["source", "target"],
          "values": ["x", "y"]
        }
      ]
    }
  ],

  "marks": [
    {
      "type": "path",
      "from": {"data": "links"},
      "encode": {
        "enter": {
          "stroke": {"value": "#ccc"},
          "strokeWidth": {"value": 2}
        },
        "update": {
          "path": {"field": "path"}
        }
      },
      "transform": [
        {
          "type": "linkpath",
          "orient": "vertical",
          "shape": "line"
        }
      ]
    },
    {
      "type": "symbol",
      "from": {"data": "nodes"},
      "encode": {
        "enter": {
          "fill": {"value": "#4587ed"},
          "size": {"value": 200}
        },
        "update": {
          "x": {"field": "x"},
          "y": {"field": "y"}
        }
      }
    },
    {
      "type": "text",
      "from": {"data": "nodes"},
      "encode": {
        "enter": {
          "text": {"field": "name"},
          "fontSize": {"value": 12},
          "align": {"value": "center"},
          "baseline": {"value": "middle"},
          "fill": {"value": "#000"}
        },
        "update": {
          "x": {"field": "x"},
          "y": {"field": "y"},
          "angle": {"value": 45}
        }
      }
    }
  ]
}

